import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "./BlogDetail.css";
import Navbar from "../Navbar/Navbar";
import { useSelector } from "react-redux";

const BlogDetail = () => {
  const { id } = useParams();
  const [blog, setBlog] = useState(null);
  const views = useSelector(state => state.blogViews[id]);

  useEffect(() => {
    axios
      .get(`https://61791a83aa7f3400174047a6.mockapi.io/v1/GetBLogs/${id}`)
      .then((response) => setBlog(response.data))
      .catch((error) => console.error(error));
  }, [id]);

  if (!blog) {
    return <div>Loading...</div>;
  }

  return (
    <>
      <Navbar />

      {/* <div className="blog-detail">
      <h2>{blog.Title}</h2>
      <p>{blog.Article}</p>
      <img src={blog.Image} title="title" className="blog-card-image" />
      <h4 className="blog-card-subtitle">{blog.Subtitle}</h4>
      <p>{blog.Article}</p>
    </div> */}
      {views !== undefined ? (
        <div className="blog-detail">
          <h2>Views: {views}</h2>
          <h2>{blog.Title}</h2>
          <p>{blog.Article}</p>
          <img src={blog.Image} title="title" className="blog-card-image" />
          <h4 className="blog-card-subtitle">{blog.Subtitle}</h4>
          <p>{blog.Article}</p>
        </div>
      ) : (
        <div>not found</div>
      )}
    </>
  );
};

export default BlogDetail;
