import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "./BlogDetail.css";
import Navbar from "../Navbar/Navbar";
import { useSelector, useDispatch } from "react-redux";
import { incrementView } from './viewsSlice'; // Import the action

const BlogDetail = () => {
  const { id } = useParams();
  const [blog, setBlog] = useState(null);
  const dispatch = useDispatch();
  const views = useSelector(state => state.blogViews[id] || 0);

  useEffect(() => {
    axios
      .get(`https://61791a83aa7f3400174047a6.mockapi.io/v1/GetBLogs/${id}`)
      .then((response) => {
        setBlog(response.data);
        // Dispatch the action to increment the view count
        dispatch(incrementView({ blogId: id }));
      })
      .catch((error) => console.error(error));
  }, [id, dispatch]);

  if (!blog) {
    return <div>Loading...</div>;
  }

  return (
    <>
      <Navbar />

      <div className="blog-detail">
        <h2>{blog.Title}</h2>
        <p>{blog.Article}</p>
        <img src={blog.Image} title="title" className="blog-card-image" />
        <h4 className="blog-card-subtitle">{blog.Subtitle}</h4>
        <p>{blog.Article}</p>
        <p>Views: {views}</p> {/* Display the view count */}
      </div>
    </>
  );
};

export default BlogDetail;
