// reducers.js

const initialState = {
    blogViews: {} 
  };

  const rootReducer = (state = initialState, action) => {
    switch (action.type) {
      case 'INCREMENT_VIEW':
        return {
          ...state,
          blogViews: {
            ...state.blogViews,
            [action.payload]: (state.blogViews[action.payload] || 0) + 1
          }
        };
      default:
        return state;
    }
  };

  export default rootReducer;
