import React, { useState } from "react";
import Context from "./Context";

export default function ContextState(props) {
  const [isMenuOpen, setIsMenuOpen] = useState(0);

  const toggleMenu = () => {
    setIsMenuOpen(isMenuOpen + 1)
  };

  return (
    <Context.Provider
      value={{
        isMenuOpen,
        toggleMenu,
      }}
    >
      {props.children}
    </Context.Provider>
  );
}
