import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "./BlogDetail.css";
import Navbar from "../Navbar/Navbar";
import { useSelector, useDispatch } from "react-redux";
import { incrementView } from '../viewsSlice'; 
import should from "../should.jpg"

const BlogDetail = () => {
  const { id } = useParams();
  const [blog, setBlog] = useState(null);
  const dispatch = useDispatch();
  const views = useSelector(state => state.blogViews[id] || 0);

  useEffect(() => {
    console.log("Fetching blog post with ID: ", id);

    axios
      .get(`https://61791a83aa7f3400174047a6.mockapi.io/v1/GetBLogs/${id}`)
      .then((response) => {
        console.log("API Response: ", response.data);
        setBlog(response.data);

        // Dispatch the action to increment the view count
        console.log("Dispatching incrementView action for blog ID: ", id);
        dispatch(incrementView(id));
      })
      .catch((error) => console.error(error));
  }, [id, dispatch]);


  if (!blog) {
    return <div>Loading...</div>;
  }

  return (
    <>
      <Navbar />
      <div class="centered-card">
  <div class="ShouldIBuyContainer">
    <div class="ShouldIBuy">
      <div class="ShouldIBuyDateAndView">
        <div class="dates">Posted on Octobar 6th 2021</div>
        <div class="views">563 views</div>
      </div>
    </div>
    <div class="ShouldIBuyText">Should I Buy a New Car or Lease a New Car in 2021?</div>
    <div class="ShouldIBuyTextContent">We provide a full range of front end mechanical repairs for all makes and models of cars, no matter the cause. This includes, We provide a full range of front end mechanical</div>
    <img src={should} title="" className="ShouldImg" alt="Should Image" />
  </div>
</div>



      <div className="blog-detail">
        <h2>{blog.Title}</h2>
        <p>{blog.Article}</p>
        <img src={blog.Image} title="title" className="blog-card-image" />
        <h4 className="blog-card-subtitle">{blog.Subtitle}</h4>
        <p>{blog.Article}</p>
        <p>Views: {views}</p> 
      </div>
    </>
  );
};

export default BlogDetail;
