import React, { useState } from "react";
import Context from "./Context";

export default function ContextState(props) {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    console.log("hamburger clicked", isMenuOpen);
    if (isMenuOpen) {
      setIsMenuOpen(false);
    } else {
      setIsMenuOpen(true);
    }
  };

  return (
    <Context.Provider
      value={{
        isMenuOpen,
        toggleMenu,
      }}
    >
      {props.children}
    </Context.Provider>
  );
}
