import React, { useState, useEffect } from "react";
import axios from "axios";
import "./blogViews.css";
import Navbar from "../Navbar/Navbar";
import should from "../should.jpg";
import { incrementBlogViews } from './BlogDetail'; 
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from 'react-redux';

const BlogDetail = () => {
  const { id } = useParams();
  const [blog, setBlog] = useState(null);
  const dispatch = useDispatch();
  // const blogViews = useSelector(state => state);
  const blogDetail = useSelector(state => state.blogDetail);


  useEffect(() => {
    console.log("Fetching blog post with ID: ", id);

    axios.get(`https://61791a83aa7f3400174047a6.mockapi.io/v1/GetBLogs/${id}`)
      .then((response) => {
        setBlog(response.data);
        dispatch(incrementBlogDetail(id)); // Dispatch the action to increment views
      });
  }, [id, dispatch]);

  if (!blog) {
    return <div>Loading...</div>;
  }

  return (
    <>
      <Navbar />
      <div class="centered-card">
        <div class="ShouldIBuyContainer">
          <div class="ShouldIBuy">
            <div class="ShouldIBuyDateAndView">
              <div class="dates">Posted on Octobar 6th 2021</div>
              <div class="views">{blogViews[id] || 0} views</div> 
            </div>
          </div>
          <div class="ShouldIBuyContainerInner">
            <div class="ShouldIBuyText"><h2>{blog.Title}</h2></div>
            <div class="ShouldIBuyTextContent">{blog.Article}</div>
          </div>
          <img src={should} title="" className="ShouldImg" alt="Should Image" />
        </div>
      </div>

      <div className="blog-detail">
        <h2>{blog.Title}</h2>
        <p>{blog.Article}</p>
        <img src={blog.Image} title="title" className="blog-card-image" />
        <h4 className="blog-card-subtitle">{blog.Subtitle}</h4>
        <p>{blog.Article}</p>
      </div>
    </>
  );
};

export default BlogDetail;
